import React, { useEffect } from 'react'
import axios from 'axios'



function Leaderboard() {


  useEffect(() => {
    axios.get('http://localhost:5000/api/scores')
    .then((response) => {
      const scores = response.data

      for (let i = 0; i < 10; i++) {
        rows.push({id: i + 1, player: scores[i].userName, score: scores[i].score})
      }
      console.log(rows);
    })
  })

  return (
    <div className="leaderboard">
        <h1 className="score">Leaderboard</h1>
        <table>
        <thead>
            <tr>
                <th>#</th>
                <th>Player</th>
                <th>Score</th>
            </tr>
        </thead>
        <tbody>
            {rows.map((row) => {
                return(
                    <tr>
                        <td>{row.id}</td>
                        <td>{row.player}</td>
                        <td>{row.score}</td>
                    </tr>
                )
            })}
        </tbody>

        </table>
    </div>
  );
}

export default Leaderboard
