import React, { useState } from 'react';
import { Container, TextField, Button } from '@material-ui/core'
import '../styles.css';
import { useLocation } from "react-router-dom"
const axios = require('axios').default

function SubmitScore() {
    const location = useLocation()
    const score = location.state?.score

    const [inputText, setInputText] = useState('')

    function handleChange(event) {
        setInputText(event.target.value)
    }

    function submit() {
        axios.post('localhost:5000/api/scores', {
            username: '',
            score: score
        })
    }

    return (
        <Container maxWidth="xs" className="form">
            <h1>{`Score: ${score}`}</h1>
            <form onSu>
                <TextField required id="username-input" label="Username" variant="filled" type="text" value={inputText} onChange={() => handleChange()} />
                <input type="submit" value="Submit to Leaderboard" />
            </form>
        </Container>
    );
}

export default SubmitScore;
